export default function Sidebar({ setSideBarOpen }) {
  // const [expanded, setExpanded] = useState(["Voucher"]); //by default opens
  const [expanded, setExpanded] = useState([""]);
  const location = useLocation();

  const toggleExpand = (itemName) => {
    setExpanded((prev) =>
      prev.includes(itemName)
        ? prev.filter((x) => x !== itemName)
        : [...prev, itemName]
    );
  };

  // Correct isActive function
  const isActive = (item) => {
    if (item.href === "/dashboard") {
      // Dashboard is active only on exact /dashboard
      return location.pathname === "/dashboard";
    } else if (item.children) {
      // Parent link active if any child route matches
      return item.children.some(
        (child) =>
          location.pathname === child.href ||
          location.pathname.startsWith(child.href + "/")
      );
    } else {
      // Other links active if exact match or starts with (for potential nested routes)
      return (
        location.pathname === item.href ||
        location.pathname.startsWith(item.href + "/")
      );
    }
  };

  return (
    // <aside className="w-64 bg-white border-r border-gray-200 flex flex-col">

    //  <aside className={`w-64`}>
    <aside className=" p-4 h-[100dvh]  bg-[#FFF8F4] text-gray-800  ">
      <div className="px-2 mb-[25.29px]">
        <img
          src="/logo.png"
          className="w-[140px] h-[42.71px] object-contain "
          loading="lazy"
        />
      </div>

      <nav className="flex-1 p-2 space-y-2 mt-10">
        {navigation.map((item) => (
          <div key={item.name}>
            {item.children ? (
              // dropdown open untill cild active
              <>
                <button
                  onClick={() => toggleExpand(item.name)}
                  className={clsx(
                    "group flex gap-x-3 w-full px-3 py-2 text-sm font-medium rounded-3xl",
                    isActive(item)
                      ? "bg-secondary-background text-primary-background"
                      : "text-gray-text-color hover:bg-primary-background hover:text-myWhite-color"
                  )}
                >
                  {item.icon}
                  <span className="flex-1 text-left">{item.name}</span>
                  {/* ---- chevron ---- */}
                  {expanded.includes(item.name) ? (
                    <FiChevronDown className="w-4 h-4 mt-1" />
                  ) : (
                    <FiChevronRight className="w-4 h-4 mt-1" />
                  )}
                </button>

                {/* NEW: stay open while any child is active */}
                {(expanded.includes(item.name) || isActive(item)) && (
                  <div className="ml-8 mt-1 space-y-1">
                    {item.children.map((child) => (
                      <NavLink
                        key={child.name}
                        to={child.href}
                        end
                        className={({ isActive }) =>
                          clsx(
                            "flex items-center px-3 py-2 text-sm rounded-lg transition",
                            isActive
                              ? "bg-secondary-background text-primary-background"
                              : "text-gray-text-color hover:bg-primary-background hover:text-myWhite-color"
                          )
                        }
                      >
                        {child.name}
                      </NavLink>
                    ))}
                  </div>
                )}
              </>
            ) : (
              <NavLink
                to={item.href}
                end={item.href === "/dashboard"} // Only use exact matching for dashboard
                className={({ isActive }) =>
                  clsx(
                    "flex items-center px-3 py-2 text-sm font-medium rounded-3xl transition",
                    isActive
                      ? "bg-secondary-background text-primary-background"
                      : "text-gray-text-color hover:bg-primary-background hover:text-myWhite-color"
                  )
                }
                onClick={() => setSideBarOpen(false)}
              >
                {/* <item.icon className="w-5 h-5 mr-3" /> */}
                {item.icon}
                <span className="ml-3 ">{item.name}</span>
              </NavLink>
            )}
          </div>
        ))}
      </nav>
    </aside>
  );
}
